/**
 * TABLE NAME -> Sea Level (Deniz Seviyesi)
 *
 * TABLE COLUMN
 *   C0  -->  id
 *   C1  -->  year: yıl
 *   C2  -->  female: toplam kadın nüfusu
 *   C3  -->  male: toplam erkek nüfusu
 */

CREATE TABLE `world_population` (    
    `id`      SMALLINT     UNSIGNED  NOT NULL AUTO_INCREMENT,   -- C0
    `year`    SMALLINT(4)  UNSIGNED  NOT NULL,                  -- C1
    `female`  BIGINT       NOT NULL,                            -- C2
    `male`    BIGINT       NOT NULL,                            -- C3
  PRIMARY KEY(`id`)
);



